n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-03-24T19:48:28.512499Z\",\"iopub.execute_input\":\"2022-03-24T19:48:28.512739Z\",\"iopub.status.idle\":\"2022-03-24T19:48:28.524915Z\",\"shell.execute_reply.started\":\"2022-03-24T19:48:28.512711Z\",\"shell.execute_reply\":\"2022-03-24T19:48:28.524373Z\"}}\ntrain.head()\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-03-24T19:48:28.526330Z\",\"iopub.execute_input\":\"2022-03-24T19:48:28.526684Z\",\"iopub.status.idle\":\"2022-03-24T19:48:28.621798Z\",\"shell.execute_reply.started\":\"2022-03-24T19:48:28.526648Z\",\"shell.execute_reply\":\"2022-03-24T19:48:28.621245Z\"}}\ncorr = train.corr()\ncorr.style.background_gradient(cmap='coolwarm')\n\n# %% [markdown]\n# **There is education_level,gender_of_respondent,cellphone_access,year,country which were correlated with bank_account so we gonna work only with those features**\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-03-24T19:48:28.622672Z\",\"iopub.execute_input\":\"2022-03-24T19:48:28.623241Z\",\"iopub.status.idle\":\"2022-03-24T19:48:28.627240Z\",\"shell.execute_reply.started\":\"2022-03-24T19:48:28.623210Z\",\"shell.execute_reply\":\"2022-03-24T19:48:28.626422Z\"}}\ntrain_cols=['education_level','gender_of_respondent','cellphone_access','year','country','bank_account']\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-03-24T19:48:28.628187Z\",\"iopub.execute_input\":\"2022-03-24T19:48:28.628406Z\",\"iopub.status.idle\":\"2022-03-24T19:48:28.652203Z\",\"shell.execute_reply.started\":\"2022-03-24T19:48:28.628371Z\",\"shell.execute_reply\":\"2022-03-24T19:48:28.651344Z\"}}\ndataset=train[train_cols]\ndataset\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-03-24T19:48:29.501110Z\",\"iopub.execute_input\":\"2022-03-24T19:48:29.501478Z\",\"iopub.status.idle\":\"2022-03-24T19:48:29.516596Z\",\"shell.execute_reply.started\":\"2022-03-24T19:48:29.501421Z\",\"shell.execute_reply\":\"2022-03-24T19:48:29.515639Z\"}}\ndataset_train=dataset.loc[0:18819][:]\ndataset_train\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-03-24T19:48:29.975234Z\",\"iopub.execute_input\":\"2022-03-24T19:48:29.975797Z\",\"iopub.status.idle\":\"2022-03-24T19:48:29.989385Z\",\"shell.execute_reply.started\":\"2022-03-24T19:48:29.975740Z\",\"shell.execute_reply\":\"2022-03-24T19:48:29.988663Z\"}}\ndataset_test=dataset.loc[18820:][:]\ndataset_test\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-03-24T19:48:29.991201Z\",\"iopub.execute_input\":\"2022-03-24T19:48:29.991695Z\",\"iopub.status.idle\":\"2022-03-24T19:48:30.001348Z\",\"shell.execute_reply.started\":\"2022-03-24T19:48:29.991650Z\",\"shell.execute_reply\":\"2022-03-24T19:48:30.000688Z\"}}\ny_train=dataset_train['bank_account']\nx_train=dataset_train.drop(['bank_account'],axis=1)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-03-24T19:48:30.010965Z\",\"iopub.execute_input\":\"2022-03-24T19:48:30.011272Z\",\"iopub.status.idle\":\"2022-03-24T19:48:30.017185Z\",\"shell.execute_reply.started\":\"2022-03-24T19:48:30.011229Z\",\"shell.execute_reply\":\"2022-03-24T19:48:30.016392Z\"}}\ny_test=dataset_test['bank_account']\nx_test=dataset_test.drop(['bank_account'],axis=1)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-03-24T19:48:30.019082Z\",\"iopub.execute_input\":\"2022-03-24T19:48:30.019521Z\",\"iopub.status.idle\":\"2022-03-24T19:48:30.101722Z\",\"shell.execute_reply.started\":\"2022-03-24T19:48:30.019486Z\",\"shell.execute_reply\":\"2022-03-24T19:48:30.100776Z\"}}\nimport seaborn as sns\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-03-24T19:48:30.487936Z\",\"iopub.execute_input\":\"2022-03-24T19:48:30.488251Z\",\"iopub.status.idle\":\"2022-03-24T19:48:30.643341Z\",\"shell.execute_reply.started\":\"2022-03-24T19:48:30.488218Z\",\"shell.execute_reply\":\"2022-03-24T19:48:30.642469Z\"}}\nsns.countplot(y_train)\n\n# %% [markdown]\n# **Imbalanced y_train**\n\n# %% [code] {\"execution\":{\"iopub.status